import React, { Component } from "react";
import { Serverurl } from "../Common/ServerUrl";
import axios from "axios";
import $ from "jquery";
import swal from "sweetalert";

export class CreateProduct extends Component {
  constructor(props) {
    super(props);
    {
      this.state = {
        token: "",
        image: null,
        name: "",
        description: "",

        stock: "",
        price: "",
        category: "",
        sub_category: "",
        discount_percent: "",
        discounted_price: "",

        error1: "",
        error2: "",
        error3: "",
        error4: "",
        error5: "",
        error6: "",
      };
    }
  }

  componentDidMount() {
    var data;
    axios({
      method: "get",
      url: Serverurl + "category_show",
      data: data,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      config: {
        headers: { "Content-Type": "application/json" },
      },
    })
      .then((res) => {
        console.log("res", res.data);
        console.log("hey", res.data);
        this.setState({
          data: res.data,
        });
        // console.log('data', res.data.data)
      })
      .catch((err) => {
        console.log(err);
        if (err) {
          // console.log('err', err.response)
          console.log({ err });
        }
      });
  }

  addProduct(e) {
    var validation = true;

    const emailsyntax = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

    if (this.state.image == "") {
      validation = false;
      this.setState({
        errorText: "*Product Image is required",
      });
    }

    if (this.state.name == "") {
      validation = false;
      this.setState({
        errorText: "*Product Name is required",
      });
    }

    if (this.state.description == "") {
      validation = false;
      this.setState({
        errorText: "*Product Description is required",
      });
    }

    if (this.state.price == "") {
      validation = false;
      this.setState({
        errorText: "*Product Price is required",
      });
    }

    if (this.state.stock == "") {
      validation = false;
      this.setState({
        errorText: "*Product stock status is required",
      });
    }

    if (this.state.category == "") {
      validation = false;
      this.setState({
        errorText: "*Product Category is required",
      });
    }

    if (this.state.sub_category == "") {
      validation = false;
      this.setState({
        errorText: "*Product Sub Category is required",
      });
    }

    if (validation == true) {
      let formData = new FormData();
      formData.append("image", this.state.image);
      formData.append("name", this.state.name);
      formData.append("description", this.state.description);
      formData.append("price", this.state.price);
      formData.append("stock", this.state.stock);
      formData.append("category", this.state.category);
      formData.append("sub_category", this.state.sub_category);

      this.setState({
        loading: true,
      });

      console.log("mydata", formData);

      var error = document.getElementById("err");
      if (this.state.name === "") {
        this.setState({
          error: "Please fill fields carefully",
        });
        error.classList.add("errorMsg");
        setTimeout(() => {
          error.classList.remove("errorMsg");
          this.setState({
            error: "",
          });
        }, 3000);
      } else {
        axios({
          method: "post",
          url: Serverurl + "products_add",
          data: formData,
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
          config: {
            headers: { "Content-Type": "application/json" },
          },
        })
          .then((res) => {
            console.log("res", res.data.data);
            if (res.status === "true") {
              swal("Poof! Your imaginary file has been deleted!", {
                icon: "success",
              });
              this.setState({
                loading: true,
              });
            } else {
              swal("Product Succesfully Created!");
            }
            setTimeout(() => {
              window.location.href = "/component/gridProducts";
            }, 3000);
            console.log("data", res.formData);
          })
          .catch((err) => {
            console.log({ err });

            console.log("res", err.response.data.error.message);

            var error_message = err.response.data.error.message;

            console.log("error", error_message[0]);

            if (err) {
              this.setState({
                loading: true,
              });
              setTimeout(() => {
                this.setState({
                  loading: false,
                });
                this.setState((willSuccess) => {
                  if (willSuccess) {
                    swal(error_message[0], {
                      icon: "warning",
                    });
                  } else {
                    swal("Your imaginary file is safe!");
                  }
                });
              }, 3000);
            }
          });
      }
    }
  }

  onKeyDown = (event) => {
    // 'keypress' event misbehaves on mobile so we track 'Enter' key via 'keydown' event
    if (event.key === "Enter") {
      event.preventDefault();
      event.stopPropagation();
      this.addAdventure();
    }
  };

  handleChangeProduct = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
      errorText: "",
    });
  };

  handleFileInput = (event) => {
    console.log(event.target.files, "file");
    console.log(event.target.files[0], "file 0");
    let file = event.target.files[0];
    this.setState({ image: file });
    if (event.target.files) {
      this.setState({
        [event.target.name]: event.target.files[0],
        file: URL.createObjectURL(event.target.files[0]),
      });
    }

    console.log(event.target.files[0]);
  };

  render() {
    return (
      <div>
        <div class="container">
          <div class="row">
            <div class="col-lg-11 col-md-12 card">
              <div class="parent">
                {/* <ul class="nav nav-tabs" id="myTab" role="tablist">
                                    <li class="nav-item">
                                        <a class="nav-link active" id="home-tab" data-toggle="tab" href="#home" role="tab" aria-controls="home" aria-selected="true">Englsh</a>
                                    </li>
                                    {/* <li class="nav-item">
