import React, { Component } from "react";
import { Serverurl } from "../Common/ServerUrl";
import axios from "axios";
import $ from "jquery";
import swal from "sweetalert";

export class UpdateBusinessUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      productData: {},
      productId: "",
      image: null,
      name: "",
      description: "",
      stock: "",
      price: "",
      category: "",
      sub_category: "",
      discount_percent: "",
      discounted_price: "",
      errorText: "",
    };
  }

  componentDidMount() {
    // var categoryData;
    var id = localStorage.getItem("productId");
    console.log("productId is: ", id);
    this.setState({
      productId: id,
    });

    axios({
      method: "get",
      url: Serverurl + "products_show/" + id,
      // data: categoryData,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      config: {
        headers: { "Content-Type": "application/json" },
      },
    })
      .then((res) => {
        console.log(
          "products_show in componentDidMount() of UpdateBrand's res.data is: ",
          res.data
        );
        this.setState({
          productData: res.data,
          //   image: res.data.image,
          //   name: res.data.name,
          //   description: res.data.description,
          //   stock: res.data.stock,
          //   price: res.data.price,
          //   category: res.data.category,
          //   discount_percent: res.data.discount_percent,
          //   discounted_price: res.data.discounted_price,
        });
        // $(document).ready(function () {
        //     $('#datatable3').DataTable();
        // });
        console.log(
          "res.data from products_show api in then() of UpdateBrand is: ",
          res.data
        );
      })
      .catch((err) => {
        this.setState({
          errorText: err.response,
        });
      });
  }

  handleChangeProduct = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
      errorText: "",
    });
  };

  handleBtnUpdateClick() {
    var formData = new FormData();
    if (this.state.image) {
      formData.append("image", this.state.image);
    }
    formData.append(
      "name",
      this.state.name ? this.state.name : this.state.productData.name
    );
    formData.append(
      "description",
      this.state.description
        ? this.state.description
        : this.state.productData.description
    );
    formData.append(
      "stock",
      this.state.stock ? this.state.stock : this.state.productData.stock
    );
    formData.append(
      "price",
      this.state.price ? this.state.price : this.state.productData.price
    );
    formData.append(
      "category",
      this.state.category
        ? this.state.category
        : this.state.productData.category
    );
    formData.append(
      "sub_category",
      this.state.sub_category
        ? this.state.sub_category
        : this.state.productData.sub_category
    );
    formData.append(
      "discount_percent",
      this.state.discount_percent
        ? this.state.discount_percent
        : this.state.productData.discount_percent
    );
    formData.append(
      "discounted_price",
      this.state.discounted_price
        ? this.state.discounted_price
        : this.state.productData.discounted_price
    );

    console.log(this.state.token);
    console.log("product's Id is: ", this.state.productId);

    axios({
      method: "post",
      url: Serverurl + "products_edit/" + this.state.productId,
      data: formData,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((res) => {
        swal("Product is successfully updated!");

        console.log(
          "res.data from products_edit/id in handleBtnUpdateClick() is: ",
          res.data
        );

        setTimeout(() => {
          window.location.href = "/component/gridProducts";
        }, 2000);
      })
      .catch((err) => {
        console.log(err.res);

        this.setState({
          errorText: "Updating product Failed! Please try again",
        });
      });
  }

  render() {
    return (
      <div>
        <div class="container">
          <div class="row">
            <div class="col-lg-11 col-md-12 card">
              <div class="parent">
                {/* <ul class="nav nav-tabs" id="myTab" role="tablist">
                                    <li class="nav-item">
                                        <a class="nav-link active" id="home-tab" data-toggle="tab" href="#home" role="tab" aria-controls="home" aria-selected="true">Englsh</a>
                                    </li>
                                    {/* <li class="nav-item">
                                        <a class="nav-link" id="profile-tab" data-toggle="tab" href="#profile" role="tab" aria-controls="profile" aria-selected="false">اردو</a>
                                    </li> 
                                </ul> */}
                <div class="tab-content" id="myTabContent">
                  <div
                    class="tab-pane fade show active"
                    id="home"
                    role="tabpanel"
                    aria-labelledby="home-tab"
                  >
                    <h4 class="mt-0 header-title">Update User</h4>

                    {/* <div class="form-group row input-margin">
                                            <label for="example-search-input" class="col-sm-2 col-form-label">Profile Image Upload</label>
                                            <div class="col-sm-12">
                                                <input type="file" id="input-file-now" name="avatar" onChange={this.handleFileInput.bind(this)} class="dropify" />
                                            </div>
                                        </div> */}
                    <div class="form-group row input-margin">
                      <label
                        for="example-text-input"
                        class="col-sm-2 col-form-label"
                      >
                        Name
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="name"
                          type="text"
                          id="example-text-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row">
                      <label
                        for="example-email-input"
                        class="col-sm-2 col-form-label"
                      >
                        Email
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="email"
                          type="email"
                          id="example-email-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row">
                      <label
                        for="example-number-input"
                        class="col-sm-2 col-form-label"
                      >
                        City
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="city"
                          type="text"
                          id="example-number-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row">
                      <label
                        for="example-password-input"
                        class="col-sm-2 col-form-label"
                      >
                        Region
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="region"
                          type="text"
                          id="example-password-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row">
                      <label
                        for="example-password-input"
                        class="col-sm-2 col-form-label"
                      >
                        Postal Code
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="postal"
                          type="number"
                          id="example-password-input"
                        />
                      </div>
                    </div>

                    {this.state.errorText ? (
                      <p style={{ color: "red" }}>{this.state.errorText}</p>
                    ) : null}
                    <div class="form-group row">
                      <div class="button-align">
                        <button
                          type="button"
                          class="btn btn-danger waves-effect waves-light submit-button"
                        >
                          Update
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default UpdateBusinessUser;
