import React, { Component } from "react";
import { Serverurl } from "../Common/ServerUrl";
import axios from "axios";
import $ from "jquery";
import swal from "sweetalert";

export class UpdateCoupon extends Component {
  constructor(props) {
    super(props);
    this.state = {
      couponData: {},
      couponId: "",
      code: "",
      discount: "",
      status: "",
      error1: "",
      error2: "",
      error3: "",
      errorText: "",
    };
  }

  componentDidMount() {
    // var categoryData;
    var id = localStorage.getItem("categoryId");
    console.log("categoryId is: ", id);
    this.setState({
      categoryId: id,
    });

    axios({
      method: "get",
      url: Serverurl + "category_show/" + id,
      // data: categoryData,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      config: {
        headers: { "Content-Type": "application/json" },
      },
    })
      .then((res) => {
        console.log(
          "category_show in componentDidMount() of UpdateCategory' res.data is: ",
          res.data
        );
        this.setState({
          categoryData: res.data,
          image: res.data.image,
          name: res.data.name,
          description: res.data.description,
        });
        // $(document).ready(function () {
        //     $('#datatable3').DataTable();
        // });
        console.log(
          "res.data from category_show api in then() of UpdateCategory is: ",
          res.data
        );
      })
      .catch((err) => {
        console.log(err);
        if (err) {
          console.log(
            "err from category_show api in UpdateCategory is: ",
            err.response
          );
          console.log({ err });
        }
      });
  }

  HandleImageInput(e) {
    console.log(e.target.files, "file");
    console.log(e.target.files[0], "file 0");
    let file = e.target.files[0];

    this.setState({
      image: file,
      error1: "",
    });

    if (e.target.files) {
      this.setState({
        [e.target.image]: e.target.files[0],
        file: URL.createObjectURL(e.target.files[0]),
      });
    }
    console.log(e.target.files[0]);
  }

  HandleNameInput(e) {
    this.setState({
      name: e.target.value,
      error2: "",
    });
  }

  HandleDescriptionInput(e) {
    this.setState({
      description: e.target.value,
      error3: "",
    });
  }

  render() {
    return (
      <div>
        <div class="container">
          <div class="row">
            <div class="col-lg-11 col-md-12 card">
              <div class="parent">
                {/* <ul class="nav nav-tabs" id="myTab" role="tablist">
                                    <li class="nav-item">
                                        <a class="nav-link active" id="home-tab" data-toggle="tab" href="#home" role="tab" aria-controls="home" aria-selected="true">Englsh</a>
                                    </li>
                                    {/* <li class="nav-item">
                                        <a class="nav-link" id="profile-tab" data-toggle="tab" href="#profile" role="tab" aria-controls="profile" aria-selected="false">اردو</a>
                                    </li> 
                                </ul> */}
                <div class="tab-content" id="myTabContent">
                  <div
                    class="tab-pane fade show active"
                    id="home"
                    role="tabpanel"
                    aria-labelledby="home-tab"
                  >
                    <h4 class="mt-0 header-title">Update Coupon</h4>
                    {/* 
                                        <div class="form-group row input-margin">
                                            <label for="example-search-input" class="col-sm-2 col-form-label">Image Upload</label>
                                            <div class="col-sm-12">
                                                <input type="file" id="input-file-now" name="avatar" onChange={this.handleFileInput.bind(this)} class="dropify" />
                                            </div>
                                        </div> */}
                    <div class="form-group row input-margin">
                      <label
                        for="example-text-input"
                        class="col-sm-2 col-form-label"
                      >
                        Code
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="code"
                          type="text"
                          id="example-text-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row input-margin">
                      <label
                        for="example-text-input"
                        class="col-sm-2 col-form-label"
                      >
                        Coupon Type
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="type"
                          type="text"
                          id="example-text-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row">
                      <label
                        for="example-email-input"
                        class="col-sm-2 col-form-label"
                      >
                        Coupon Amount
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="amount"
                          type="text"
                          id="example-email-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row input-margin">
                      <label
                        for="example-text-input"
                        class="col-sm-2 col-form-label"
                      >
                        Discription
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="discription"
                          type="text"
                          id="example-text-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row input-margin">
                      <label
                        for="example-text-input"
                        class="col-sm-2 col-form-label"
                      >
                        Product IDs
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="id"
                          type="text"
                          id="example-text-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row input-margin">
                      <label
                        for="example-text-input"
                        class="col-sm-2 col-form-label"
                      >
                        Usage / Limit
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="limit"
                          type="text"
                          id="example-text-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row input-margin">
                      <label
                        for="example-text-input"
                        class="col-sm-2 col-form-label"
                      >
                        Expiry Date
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="expiry"
                          type="date"
                          id="example-text-input"
                        />
                      </div>
                    </div>

                    <div class="form-group row">
                      <div class="button-align">
                        <button
                          type="button"
                          class="btn btn-danger waves-effect waves-light submit-button"
                        >
                          Update
                        </button>
                      </div>
                    </div>
                    {/* {this.state.errorText ?
                                            <p style={{ color: 'red' }}>{this.state.errorText}</p>
                                            : null
                                        }
                                        <div id="err">{this.state.error}</div> */}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default UpdateCoupon;
