import React, { Component } from "react";
import { Serverurl } from "../Common/ServerUrl";
import axios from "axios";
import $ from "jquery";
import swal from "sweetalert";

export class CreateProduct extends Component {
  constructor(props) {
    super(props);
    {
      this.state = {
        token: "",
        image: null,
        name: "",
        description: "",
        stock: "",
        price: "",
        category: "",
        sub_category: "",
        discount_percent: "",
        discounted_price: "",

        error1: "",
        error2: "",
        error3: "",
        error4: "",
        error5: "",
        error6: "",
        error7: "",
      };
    }
  }

  async componentDidMount() {
    var token = await localStorage.getItem("token");
    this.setState({
      token,
    });

    console.log("componentDidMount() token: ", this.state.token);
  }

  handleImageInput(e) {
    console.log(e.target.files, "file");
    console.log(e.target.files[0], "file 0");
    let file = e.target.files[0];

    this.setState({
      image: file,
      error1: "",
    });

    if (e.target.files) {
      this.setState({
        [e.target.image]: e.target.files[0],
        file: URL.createObjectURL(e.target.files[0]),
      });
    }
    console.log(e.target.files[0]);
  }

  handleNameInput(e) {
    this.setState({
      name: e.target.value,
      error2: "",
    });
  }

  handleDescriptionInput(e) {
    this.setState({
      description: e.target.value,
      error3: "",
    });
  }

  handleStockInput(e) {
    this.setState({
      stock: e.target.value,
      error4: "",
    });
  }

  handlePriceInput(e) {
    this.setState({
      price: e.target.value,
      error5: "",
    });
  }

  handleCategoryInput(e) {
    this.setState({
      category: e.target.value,
      error6: "",
    });
  }

  handleSubCategoryInput(e) {
    this.setState({
      sub_category: e.target.value,
      error7: "",
    });
  }

  handleBtnSubmitClick() {
    var validation = true;

    if (this.state.image == null) {
      this.setState({
        error1: "*Product image is required!",
      });
      validation = false;
    }

    if (this.state.name == "") {
      this.setState({
        error2: "*Product name is required!",
      });
      validation = false;
    }

    if (this.state.description == "") {
      this.setState({
        error3: "*Product description is required!",
      });
      validation = false;
    }

    if (this.state.stock == "") {
      this.setState({
        error4: "*Product stock is required!",
      });
      validation = false;
    }

    if (this.state.price == "") {
      this.setState({
        error5: "*Product price is required!",
      });
      validation = false;
    }

    if (this.state.category == "") {
      this.setState({
        error6: "*Product category is required!",
      });
      validation = false;
    }

    if (this.state.sub_category == "") {
      this.setState({
        error7: "*Product sub_category is required!",
      });
      validation = false;
    }

    if (validation == true) {
      let formData = new FormData();
      formData.append("image", this.state.image);
      formData.append("name", this.state.name);
      formData.append("description", this.state.description);
      formData.append("stock", this.state.stock);
      formData.append("price", this.state.price);
      formData.append("category", this.state.category);
      formData.append("sub_category", this.state.sub_category);

      this.setState({
        loading: true,
      });

      console.log("formData of CreateProduct is: ", formData);

      axios({
        method: "post",
        url: Serverurl + "products_add",
        data: formData,
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        config: {
          headers: { "Content-Type": "application/json" },
        },
      })
        .then((res) => {
          console.log("res.data of products_add API is: ", res.data);

          swal("Product Succesfully Added!");

          setTimeout(() => {
            window.location.href = "/component/gridProducts";
          }, 3000);
        })
        .catch((err) => {
          console.log("Error from products_add API is: ", err);
        });
    }
