import React, { Component } from "react";
import { Serverurl } from "../Common/ServerUrl";
import axios from "axios";
import $ from "jquery";
import swal from "sweetalert";

export class UpdateProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      productData: {},
      productId: "",
      image: null,
      name: "",
      description: "",
      stock: "",
      price: "",
      category: "",
      sub_category: "",
      discount_percent: "",
      discounted_price: "",

      error1: "",
      error2: "",
      error3: "",
      error4: "",
      error5: "",
      error6: "",
      error7: "",
      errorText: "",
    };
  }

  componentDidMount() {
    // var categoryData;
    var id = localStorage.getItem("productId");
    console.log("productId is: ", id);
    this.setState({
      brandId: id,
    });

    axios({
      method: "get",
      url: Serverurl + "products_show/" + id,
      // data: categoryData,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      config: {
        headers: { "Content-Type": "application/json" },
      },
    })
      .then((res) => {
        console.log(
          "products_show in componentDidMount() of UpdateBrand's res.data is: ",
          res.data
        );
        this.setState({
          productData: res.data,
          image: res.data.image,
          name: res.data.brand_name,
          description: res.data.description,
          stock: res.data.stock,
          price: res.data.price,
          category: res.data.category,
          discount_percent: res.data.discount_percent,
          discounted_price: res.data.discounted_price,
        });
        // $(document).ready(function () {
        //     $('#datatable3').DataTable();
        // });
        console.log(
          "res.data from products_show api in then() of UpdateBrand is: ",
          res.data
        );
      })
      .catch((err) => {
        console.log(err);
        if (err) {
          console.log(
            "err from products_show api in UpdateBrand is: ",
            err.response
          );
          console.log({ err });
        }
      });
  }

  handleImageInput(e) {
    console.log(e.target.files, "file");
    console.log(e.target.files[0], "file 0");
    let file = e.target.files[0];

    this.setState({
      image: file,
      error1: "",
    });

    if (e.target.files) {
      this.setState({
        [e.target.image]: e.target.files[0],
        file: URL.createObjectURL(e.target.files[0]),
      });
    }
    console.log(e.target.files[0]);
  }

  handleNameInput(e) {
    this.setState({
      name: e.target.value,
      error2: "",
    });
  }

  handleDescriptionInput(e) {
    this.setState({
      description: e.target.value,
      error3: "",
    });
  }

  handleStockInput(e) {
    this.setState({
      stock: e.target.value,
      error4: "",
    });
  }

  handlePriceInput(e) {
    this.setState({
      price: e.target.value,
      error5: "",
    });
  }

  handleCategoryInput(e) {
    this.setState({
      category: e.target.value,
      error3: "",
    });
  }

  handleBtnUpdateClick() {
    var validation = true;

    if (this.state.image == null) {
      this.setState({
        error1: "*Product image is required!",
      });
      validation = false;
    }

    if (this.state.name == "") {
      this.setState({
        error2: "*Product name is required!",
      });
      validation = false;
    }

    if (this.state.description == "") {
      this.setState({
        error3: "*Product description is required!",
      });
      validation = false;
    }

    if (this.state.stock == "") {
      this.setState({
        error4: "*Product stock is required!",
      });
      validation = false;
    }

    if (this.state.price == "") {
      this.setState({
        error5: "*Product price is required!",
      });
      validation = false;
    }

    if (this.state.category == "") {
      this.setState({
        error6: "*Brand category is required!",
      });
      validation = false;
    }

    if (validation == true) {
      var formData = new FormData();
      formData.append("image", this.state.image);
      formData.append("name", this.state.name);
      formData.append("description", this.state.description);
      formData.append("stock", this.state.stock);
      formData.append("price", this.state.price);
      formData.append("category", this.state.category);
      formData.append("discount_percent", this.state.discount_percent);
      formData.append("discounted_price", this.state.discounted_price);

      console.log(this.state.token);
      console.log("product's Id is: ", this.state.productId);

      axios({
        method: "post",
        url: Serverurl + "products_edit/" + this.state.productId,
        data: formData,
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
        .then((res) => {
          swal("Brand is successfully updated!");

          console.log(
            "res.data from products_edit/id in handleBtnUpdateClick() is: ",
            res.data
          );

          window.location.href = "/component/gridProducts";
        })
        .catch((err) => {
          console.log({ err });
          if (err) {
            // error.classList.add('errorMsg');
            console.log(err.res);

            this.setState({
              errorText: "Updating product Failed! Please try again",
              //   loading: true,
            });
            // error.classList.add('errorMsg');
            // setTimeout(() => {
            //     error.classList.remove('errorMsg')
            //     this.setState({
            //         error: '',
            //         loading: false
            //     })
            // }, 3000)
          }
        });
      // }
    }
  }

  render() {
    return (
      <div>
        <div class="container">
          <div class="row">
            <div class="col-lg-11 col-md-12 card">
              <div class="parent">
                {/* <ul class="nav nav-tabs" id="myTab" role="tablist">
                                    <li class="nav-item">
                                        <a class="nav-link active" id="home-tab" data-toggle="tab" href="#home" role="tab" aria-controls="home" aria-selected="true">Englsh</a>
                                    </li>
                                    {/* <li class="nav-item">
                                        <a class="nav-link" id="profile-tab" data-toggle="tab" href="#profile" role="tab" aria-controls="profile" aria-selected="false">اردو</a>
                                    </li> 
                                </ul> */}
                <div class="tab-content" id="myTabContent">
                  <div
                    class="tab-pane fade show active"
                    id="home"
                    role="tabpanel"
                    aria-labelledby="home-tab"
                  >
                    <h4 class="mt-0 header-title">Update Product</h4>

                    <div class="form-group banner-position input-margin">
                      <label for="example-search-input" class="col-form-label">
                        Product Image
                      </label>
                      <div>
                        <input
                          type="file"
                          name="image"
                          onChange={this.handleImageInput.bind(this)}
                          class="form-control-file"
                        />
                      </div>
