import React, { Component } from "react";
import { Serverurl } from "../Common/ServerUrl";
import axios from "axios";
import $ from "jquery";
import swal from "sweetalert";

export class UpdateOrder extends Component {
  constructor(props) {
    super(props);
    this.state = {
      orderData: {},

      orderId: "",

      name: "",
      email: "",
      address: "",
      country: "",
      city: "",
      phone: "",
      postal_code: "",
      date: "",
      status: "",
      total: "",

      errorText: "",
    };
  }

  componentDidMount() {
    var id = localStorage.getItem("orderId");
    console.log("orderId is: ", id);
    this.setState({
      orderId: id,
    });

    axios({
      method: "get",
      url: Serverurl + "orders_show/" + id,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      config: {
        headers: { "Content-Type": "application/json" },
      },
    })
      .then((res) => {
        console.log(
          "orders_show in componentDidMount() of UpdateOrder's res.data is: ",
          res.data
        );
        this.setState({
          orderData: res.data,
        });

        console.log(
          "res.data from orders_show api in then() of UpdateOrder is: ",
          res.data
        );
      })
      .catch((err) => {
        this.setState({
          errorText: err.response,
        });
      });
  }

  handleChangeProduct = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
      errorText: "",
    });
  };

  handleBtnUpdateClick() {
    var formData = new FormData();

    formData.append(
      "name",
      this.state.name ? this.state.name : this.state.orderData.name
    );
    formData.append(
      "email",
      this.state.email ? this.state.email : this.state.orderData.email
    );
    formData.append(
      "address",
      this.state.address ? this.state.address : this.state.orderData.address
    );
    formData.append(
      "country",
      this.state.country ? this.state.country : this.state.orderData.country
    );
    formData.append(
      "city",
      this.state.city ? this.state.city : this.state.orderData.city
    );
    formData.append(
      "postal_code",
      this.state.postal_code
        ? this.state.postal_code
        : this.state.productData.postal_code
    );
    formData.append(
      "phone",
      this.state.phone ? this.state.phone : this.state.productData.phone
    );
    formData.append(
      "date",
      this.state.date ? this.state.date : this.state.productData.date
    );
    formData.append(
      "status",
      this.state.status ? this.state.status : this.state.productData.status
    );

    formData.append(
      "total",
      this.state.total ? this.state.total : this.state.productData.total
    );

    console.log(this.state.token);
    console.log("product's Id is: ", this.state.productId);

    axios({
      method: "post",
      url: Serverurl + "products_edit/" + this.state.productId,
      data: formData,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((res) => {
        swal("Product is successfully updated!", {
          icon: "success",
        });

        console.log(
          "res.data from products_edit/id in handleBtnUpdateClick() is: ",
          res.data
        );

        setTimeout(() => {
          window.location.href = "/component/gridProducts";
        }, 2000);
      })
      .catch((err) => {
        console.log(err.res);

        this.setState({
          errorText: "Updating product Failed! Please try again",
        });
      });
  }

  render() {
    return (
      <div>
        <div class="container">
          <div class="row">
            <div class="col-lg-11 col-md-12 card">
              <div class="parent">
                {/* <ul class="nav nav-tabs" id="myTab" role="tablist">
                                    <li class="nav-item">
                                        <a class="nav-link active" id="home-tab" data-toggle="tab" href="#home" role="tab" aria-controls="home" aria-selected="true">Englsh</a>
                                    </li>
                                    {/* <li class="nav-item">
                                        <a class="nav-link" id="profile-tab" data-toggle="tab" href="#profile" role="tab" aria-controls="profile" aria-selected="false">اردو</a>
                                    </li> 
                                </ul> */}
                <div class="tab-content" id="myTabContent">
                  <div
                    class="tab-pane fade show active"
                    id="home"
                    role="tabpanel"
                    aria-labelledby="home-tab"
                  >
                    <h4 class="mt-0 header-title">Update Product</h4>

                    <div class="form-group row input-margin">
                      <label
                        for="example-text-input"
                        class="col-sm-2 col-form-label"
                      >
                        Name
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="name"
                          type="text"
                          id="example-text-input"
                          onChange={this.handleChangeProduct.bind(this)}
                          placeholder={this.state.productData.name}
                        />
                      </div>
                    </div>

                    <div class="form-group row input-margin">
                      <label
                        for="example-text-input"
                        class="col-sm-2 col-form-label"
                      >
                        Description
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="description"
                          type="text"
                          id="example-text-input"
                          onChange={this.handleChangeProduct.bind(this)}
                          placeholder={this.state.productData.description}
                        />
                      </div>
                    </div>

                    <div class="form-group row input-margin">
                      <label
                        for="example-text-input"
                        class="col-sm-2 col-form-label"
                      >
                        Stock
                      </label>
                      <div class="col-sm-10">
                        <input
                          class="form-control"
                          name="stock"
                          type="text"
                          id="example-text-input"
                          onChange={this.handleChangeProduct.bind(this)}
                          placeholder={this.state.productData.stock}
                        />
                      </div>
                    </div>

                    <div class="form-group row">
                      <label class="col-sm-2 col-form-label">Category</label>
                      <div class="col-sm-10">
                        <select
                          name="category_id"
                          class="form-control"
