import avatar2 from "../component/assets/images/users/avatar2.jpg";
import React, { Component } from "react";
import { Serverurl, Serverurlimg } from "../Common/ServerUrl";
import axios from "axios";
import $ from "jquery";

export class GridCoupon extends Component {
  constructor(props) {
    super(props);
    this.state = {
      couponData: [],
    };
  }

  componentDidMount() {
    // var data;
    axios({
      method: "get",
      url: Serverurl + "coupon_show",
      // data: data,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      config: {
        headers: { "Content-Type": "application/json" },
      },
    })
      .then((res) => {
        console.log("coupon_show api response.data is: ", res.data);

        this.setState({
          couponData: res.data,
        });

        $(document).ready(function () {
          $("#datatable2").DataTable();
        });

        // console.log('data', res.data.data)
      })
      .catch((err) => {
        console.log("Error from coupon_show api is: ", err);
      });
  }

  render() {
    return (
      <div>
        <div className="container">
          <div class="row justify-content-center">
            <div class="col-lg-11 col-sm-11">
              <div class="card">
                <div class="card-body table-responsive">
                  <div className="list-head-btn">
                    <div className="head">
                      <h4>Coupons List</h4>
                    </div>
                    <div class="button-align">
                      <a
                        href="/component/CreateCoupon"
                        type="button"
                        class="btn btn-danger waves-effect waves-light submit-button"
                      >
                        Add Coupon
                      </a>
                    </div>
                  </div>
                  <div class="table-3">
                    <table id="datatable2" class="table">
                      <thead>
                        <tr>
                          <th>Id</th>
                          <th>Code</th>
                          <th>Discount</th>
                          <th>Status</th>
                          <th>Actions</th>
                        </tr>
                      </thead>

                      <tbody>
                        <tr>
                          {this.state.productData.map((product) => (
                            <tr key={product.id}>
                              <td>
                                <img src={product.image} width="70px" />
                              </td>
                              <td>{product.name}</td>
                              <td>{product.description}</td>
                              <td>{product.stock}</td>
                              <td>Rs. {product.price}</td>
                              <td>{product.discount_percent} %</td>
                              <td>Rs. {product.discounted_price}</td>

                              <td>
                                <div class="icon-pad">
                                  <a
                                    href="/component/updateProduct"
                                    onClick={this.updateProduct.bind(
                                      this,
                                      product.id
                                    )}
                                  >
                                    <i className="fas fa-pencil-alt"></i>
                                  </a>
                                  <i
                                    className="fas fa-trash-alt"
                                    onClick={this.deleteProductHandler.bind(
                                      this,
                                      product.id
                                    )}
                                  ></i>
                                </div>
                              </td>
                            </tr>
                          ))}
                        </tr>
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default GridCoupon;
