import React, { Component } from "react";
import { Serverurl } from "../Common/ServerUrl";
import axios from "axios";
import $ from "jquery";
import swal from "sweetalert";

export class UpdateOrder extends Component {
  constructor(props) {
    super(props);
    this.state = {
      orderData: {},

      name: "",
      email: "",
      address: "",
      country: "",
      city: "",
      phone: "",
      postal_code: "",
      date: "",
      stattus: "",
      total: "",

      errorText: "",
    };
  }

  componentDidMount() {
    var id = localStorage.getItem("productId");
    console.log("productId is: ", id);
    this.setState({
      productId: id,
    });

    axios({
      method: "get",
      url: Serverurl + "orders_show/" + id,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      config: {
        headers: { "Content-Type": "application/json" },
      },
    })
      .then((res) => {
        console.log(
          "orders_show in componentDidMount() of UpdateOrder's res.data is: ",
          res.data
        );
        this.setState({
          orderData: res.data,
        });

        console.log(
          "res.data from orders_show api in then() of UpdateOrder is: ",
          res.data
        );
      })
      .catch((err) => {
        this.setState({
          errorText: err.response,
        });
      });

    //category_show api
    axios({
      method: "get",
      url: Serverurl + "category_show",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      config: {
        headers: { "Content-Type": "application/json" },
      },
    })
      .then((res) => {
        console.log("res.data of category_show api is: ", res.data);
        this.setState({
          categoryData: res.data,
        });
      })
      .catch((err) => {
        console.log(err);
      });

    // subcategory_show api
    axios({
      method: "get",
      url: Serverurl + "subcategory_show",
      //   data: data,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      config: {
        headers: { "Content-Type": "application/json" },
      },
    })
      .then((res) => {
        console.log("subcategory_show api response.data is: ", res.data);

        this.setState({
          subCategoryData: res.data,
        });
      })
      .catch((err) => {
        console.log("sub-category .catch error is: ", err);
      });

    //brand_show api
    axios({
      method: "get",
      url: Serverurl + "brand_show",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      config: {
        headers: { "Content-Type": "application/json" },
      },
    })
      .then((res) => {
        console.log("res.data of brand_show api is: ", res.data);
        this.setState({
          brandData: res.data,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  }

  handleChangeProduct = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
      errorText: "",
    });
  };

  handleBtnUpdateClick() {
    var formData = new FormData();

    formData.append(
      "name",
      this.state.name ? this.state.name : this.state.productData.name
    );
    formData.append(
      "description",
      this.state.description
        ? this.state.description
        : this.state.productData.description
    );
    formData.append(
      "stock",
      this.state.stock ? this.state.stock : this.state.productData.stock
    );
    formData.append(
      "category_id",
      this.state.category_id
        ? this.state.category_id
        : this.state.productData.category_id
    );
    formData.append(
      "subcategory_id",
      this.state.subcategory_id
        ? this.state.subcategory_id
        : this.state.productData.subcategory_id
    );
    formData.append(
      "brand_id",
      this.state.brand_id
        ? this.state.brand_id
        : this.state.productData.brand_id
    );
    formData.append(
      "price",
      this.state.price ? this.state.price : this.state.productData.price
    );
    formData.append(
      "discount_percent",
      this.state.discount_percent
        ? this.state.discount_percent
        : this.state.productData.discount_percent
    );
    formData.append(
      "discounted_price",
      this.state.discounted_price
        ? this.state.discounted_price
        : this.state.productData.discounted_price
    );

    console.log(this.state.token);
    console.log("product's Id is: ", this.state.productId);

    axios({
      method: "post",
